INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('a89780c4-da03-4d8f-a92d-974e101c3d9d', '76c9381e-690f-4728-8746-c64f59915256', 'What is the procedure adopted for recovery and disposal of the tank bottom sludge?', 'As per sludge disposal procedure/guidelines of the Company. Presently location is having 84 KL sludge since 2009 waiting for disposal.', 'Location to initiate action for disposal of sludge.', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'STATUTORY', 'Action initiated and write-off approval note  sent to CFD, however returned in Dec''16 by HQ advising to take action as per the new DRA. Process started.', 2, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('a92f032e-7132-46d0-a87b-a9805da1be27', 'a678a6d8-81a9-43b6-8c37-16e9683c533b', '', 'The type of Support which has been provided is a fixed support , which is not correct. Support should be a  saddle support on roller to accommodate Thermal expansion.', 'The fixed supports may be replaced by saddle support on Roller to accommodate Thermal expansion. Saddle has to be tack welded with the pipe.', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'DESIGN RELATED', 'Saddle and Roller supports is not the norm/practidce Of Industry at VIZAG INSTALLATION. Even IOC/Refinery/HPC marketing are not having Roller supports.Even recently constructed HPC marketing Terminal(Petropark) is not having Roller supports. Kindly take up the matter with OISD for dropping this point.', 5, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('a9cf2804-646a-439a-bea9-4cdf08476217', 'd7529a6c-66da-44b3-b6e2-073f09bbea0b', 'Whether the pumps are in auto or manual mode.', 'Pumps are in manual mode.', 'To be made in auto mode at the earliest, as prescribed in  OISD STD-117.(AP 58)', 'PENDING', '2017-09-30 00:00:00', '2017-09-30 00:00:00', 0, 'MBLR', 'Work in progress', 7, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('aab7e871-57b5-46a0-9205-73655515d65c', '5129cfec-f580-4173-bf41-eed305ce5620', 'Fire water storage and hydrant connection from mutual aid members.', 'Fire water storage is 3x 1200 KL against requirement of 3280 KL (single contingency) and meets the requirement.  Hydrant connection from adjacent mutual aid member (IOCL) on delivery side thru'' siding line which will not be able to receive water directly into water tank', 'Hydrant connection to be made to receive fire water directly in water tank', 'PENDING', '2017-05-31 00:00:00', '2017-06-30 00:00:00', 0, 'DESIGN RELATED', ' WORK IN PROGRESS', 3, null, true);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('aabb1340-279b-4670-993f-e32bc8f77bc7', 'f0961207-9b95-4e75-bfb9-081db4b74010', 'Whether suitable valves are provided on gantry header lines near gantry at safe distance for quick isolation?', 'Presently gate valves are provided. MOV''s has been planned in automation revamp of the location.', 'To comply', 'PENDING', '2017-04-30 00:00:00', '2017-04-30 00:00:00', 0, 'MBLR', 'work in progress', 26, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('aadcda3f-f35b-4e9a-bb5b-144809e9278e', '36f77b46-99e1-4ed6-b089-b785d17a9733', 'General', 'Inter distance from tank to boundary wall do not meet the OISD norms. Further, many dwelling units have come up close to the boundary wall on two sides that may pose security threat', 'Issue needs to be addressed and appropriate measures taken', 'PENDING', '2017-05-31 00:00:00', '2017-05-31 00:00:00', 0, 'INFEASIBLE', '1. Public awareness programs are conducted on regular basis to enhance dangers posed.  2. QRA done, 3. Mitigation measures under implementation', 9, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('ac013a72-e44f-4a4c-a5eb-e2d355e892c4', 'd622ed3e-6de0-4047-89f1-098cea78ad88', 'Whether all facilities are appearing in PESO approved layout drawing and license number is displayed?', 'License no is displated. PESO approved drawing does not incl light fittings provided inside the dyke near manifold area. Newly constructed toilet block in TT parking area has not been incorporated. Location has applied for final approval from PESO for Toilet block whic h is awaited.', 'Corrective action to be taken.', 'COMPLIED', '2017-04-30 00:00:00', '2017-06-05 10:56:11', 0, 'STATUTORY', 'Work has been started', 2, '2017-04-30 00:00:00.000000', false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('acaae6f0-4d22-4a14-b870-781f49c3ce8b', '1bbe800f-da9f-4e97-9ada-396697cf7d56', '', 'All officers and staff (except clerical staff) are covered for Live Fire fighting training. However, security guards are not covered.', 'All company staff and security staff must be covered for live fire fighting training.', 'PENDING', '2017-05-30 00:00:00', '2017-05-30 00:00:00', 0, 'OTHERS', '', 9, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('ace96a81-7f49-4c14-bb5b-3431b6a7404e', '0c9fc4c3-a3d1-4fea-85dc-9f6537174b14', 'Water network should be continuous flow all the time instead of isolating the supply by each company.  Hydrant should have a return line to ensure proper hydraulics.', '', 'Will be taken up with Industry.', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'OTHERS', 'OMC to undertake the work. Tendering completed and work awarded to M/s. Balmer Lawrie', 2, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('ad711c42-d7ce-4923-a286-3985397b5f7e', '36f77b46-99e1-4ed6-b089-b785d17a9733', 'Tank wagon siding', 'There is no hydrant network around the siding. Few hydrants points provided outside of depot are considered inadequate & will not meet the requirement in emergency. Further, half of the siding area is without hydrant coverage', 'Necessary corrective measures to be taken urgently.', 'PENDING', '2017-09-30 00:00:00', '2017-09-30 00:00:00', 0, 'INFEASIBLE', 'Various risk mitigation options being contemplated for execution to comply recommendation', 3, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('ae668f2b-cb85-4286-a38b-e166cad1bb6a', 'ffe732ea-2442-444c-8ede-ccc952141d51', 'Tank Truck loading and unloading area', 'Ladder has been provided without swing action because of no counter weight and no hand raling has been provided. Rubber pads /neoprene pads has not been provided to avoid friction spark. Present position of ladder is very unsafe.', 'Proper swing ladder with counter weight with rubber pads needs to be provided.', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'DESIGN RELATED', 'Budget planning done for new 6 bay gantry with new swing ladders & counter weight.', 1, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b0c6b013-a624-4106-95c1-96f2814460c5', '1bbe800f-da9f-4e97-9ada-396697cf7d56', '', '2nd external audit was carried out in 2013. Some recoomendations are pending for compliance.', 'OISD audit recommendations need to be compled', 'PENDING', '2017-09-30 00:00:00', '2017-09-30 00:00:00', 0, 'MBLR', 'Job in progress for compliance of various pending recommendations', 0, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b1bc22c4-c64c-459e-848b-6fe30c8b229a', 'd138c049-baab-4690-b1c6-ad96576381e0', 'Tank lorry filling Gantry', 'Location is having 4 nos. tank lorry filling gantry. Load 100 tank lorry per day on average in two shift. Top loading operation is done through rubber hoses and aluminum spout manually without any mechanical flow meter. No ESD has been provided in the gantry.', 'Flow meter needs to be installed in the gantry. Loading arm to be put in the place instead of rubber hose and aluminum spout. ESD to be provided at the gantry.', 'PENDING', '2017-09-30 00:00:00', '2017-09-30 00:00:00', 0, 'DESIGN RELATED', 'We have shifted Flowmeters from MUGHALSARAI TOP. Proposal to be made..Job in pre-tendering stage', 5, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b201ff44-1819-44e7-b4e9-6f7e08b29669', '6d3dff7d-e5e2-4302-a828-542608a91a5d', 'D) Tank Farm', 'Fire break wall has been provided segregating individual tank area, however, height of the fire break wall is not as prescribed in relevant STD.  Further, one large open drain in the dyke is connecting two fire break wall without any isolation valve which defeat the purpose of fire break wall', 'Height of fire break wall shall be as prescribed in OISD STD-118, clause 7.1.3.a.  Suitable measure to be taken to prevent spills from one tank endangering any other tank in the same Enclosure.', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'DESIGN RELATED', 'wip', 0, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b2fac08b-c83b-4051-a8a0-525b110c0b75', '5129cfec-f580-4173-bf41-eed305ce5620', 'PESO approval beyond sunset operations.', 'Operation at siding continues beyond sunset without PESO approval.  Location applied for permission  from PESO to work beyond sunset.', 'Pending PESO approval.  MOC to be taken from appropriate authority.', 'PENDING', '2017-04-30 00:00:00', '2017-08-30 00:00:00', 0, 'STATUTORY', ' WORK IN PROGRESS', 1, null, true);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b41043bb-7dc0-4e51-b7b2-29ea36821e3f', '25d35a2e-04fd-4a00-a2b2-dbbd54c4c62b', 'Whether cross checking of auto level gauge is done through manual gauge, while the tanks are under operation.', 'Only manual gauginng being done.', 'Auto level gauge to be provided.', 'PENDING', '2017-08-31 00:00:00', '2017-08-31 00:00:00', 0, 'MBLR', 'MBLR works in HFHSD tank T-3 is in progress. MBLR works in FO tank T-6 is pending. FO tank no T-6 is having off spec stock of 1470 KL. This stock is planned to be evacuated and sent to Kochi refinery.', 2, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b4768ee4-7449-4f4c-8aa2-0edbfeffe883', 'd7529a6c-66da-44b3-b6e2-073f09bbea0b', 'Whether foam pourers are provided as per OISD 117 for
 (a) Fixed roof
 (b) Floating roof', 'Foam pourers have been provided in all floating roof tanks and tank nos. 4 & 5 (fixed roof).  Foam pourer functioning tested for tank no. 21 and was found to be in order.', 'Since inter tank distances are not meeting, in tank farm no. 4, all cone roof tanks to be provided with foam pourers as per OISD -117.(AP 37)', 'PENDING', '2017-09-30 00:00:00', '2017-09-30 00:00:00', 0, 'MBLR', 'Completed in all tanks except LDO tanks TK1 & TK3, & MS tank Tk23.
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b496ba27-94c3-4d98-bd76-34e4b2490516', 'd7529a6c-66da-44b3-b6e2-073f09bbea0b', 'Whether sprinkler system is provided for tanks as per OISD-117.
 (a) Fixed roof
 (b) Floating roof', 'Inter tank distance of tank no. 4, 5, 6, 7, 8, 9, 10 & 20 (fixed roof tanks) are not meeting the OISD 118 requirements. Moreover, all the above tanks have not been provided with sprinkler system.   Floating roof tank nos. 21, 22, 23, & 24 have been provided with sprinkler system. All the tanks were tested during inspection and found to be working satisfactorily.    However, sprinkler nozzles have not been provided below the stairway and water coverage is not adequate.', 'Sprinkler system to be provided in line with OISD – 117 (4.2.7). (AP 34)  Tanks not meeting the inter tank distances should be provided with fixed foam / semi fixed foam system in line with OISD – 117 (4.2.10). (AP 35) Additional nozzles to be provided below the stairway for uniform wetting of the tank shell. (AP 36)', 'PENDING', '2017-09-30 00:00:00', '2017-09-30 00:00:00', 0, 'MBLR', 'This special product is being shifted out of BIJWASAN INSTALLATION installation.', 3, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b49ec24f-6e50-461c-b6d5-8f8b157a1930', '6fffd6bc-bfef-4d98-a164-016a9936d47d', 'Safety Management system', 'Fire drill records have been checked for the period 29.06.16 till 28.10.16.Location has conducted fire drills on monthly basis as well as at common TW siding.Reports are available on record.Shortcomings has also been complied .Fire drill timing matches with Fire engine log book register.However,location is yet to conduct Off site fire drill in co-ordination with local administration and during Off shift hours', 'Location to conduct Off site drill in co-ordination with local administration as well as off shift drill.Security guards needs to be trained for handling fire fighting equipments effectively', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'OTHERS', 'We are continuously following up with Dy Director of Factories.Last follow ups were done on 22.03.17 and 24.04.17.Updation of Off-Site Emergency Plan of Belagavi District is under progress,and meeting regarding the same was attended on 24.04.17 at Factory Inspector office.After the Approval of Off-site Emergency Plan,Dy Director of Factories will give date for the Off-site drill', 0, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b54dec52-c236-4697-bba6-1fc03769a20c', '5129cfec-f580-4173-bf41-eed305ce5620', 'Foam pourer for MS tank 14', 'Flange joints of foam pourers for tank No. 14 was fitted at inaccessable height from pathway floor.  Thus, reversing of face at a height of 2.5 mtrs without platform/railing will be unsafe which will be required for periodical testing of foam pourers.', 'To be reviewed for corrective action', 'PENDING', '2017-05-31 00:00:00', '2017-06-30 00:00:00', 0, 'DESIGN RELATED', 'E&P WORK IN PROGRESS', 4, null, true);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b67f23eb-147d-410e-8613-f0dfa693fb1b', 'f0961207-9b95-4e75-bfb9-081db4b74010', 'Whether TSV / vent lines are provided on suction and discharge lines to take care line expansion due to atmop. temp rise?', 'No such arrangement is available both at TW PH & TLF PH to take care of product expansion', 'Expansion lines to be connected with the blocked portion between pump and Ist valve to take care thermal expansion.', 'PENDING', '2017-09-30 00:00:00', '2017-09-30 00:00:00', 0, 'DESIGN RELATED', 'Completed along with pump augmentation works', 23, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b7714ad4-a5ce-4409-a6d6-9fa0cb25e7f0', '5dae3389-cf84-433e-b0e8-8f120a60506e', 'Whether suitable circuit breakers are provided at the point of commencement of supply easily accessible and operable to isolate the entire incoming supply?', 'Circuit breakers not provided', 'VCBs to be provided on HT side', 'PENDING', '2017-02-28 00:00:00', '2017-02-28 00:00:00', 0, 'DESIGN RELATED', 'Proposal under contingency has been prepared and sent to region for necessary approval.', 1, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b9b0b453-b61a-4d4b-9f5c-6dd7a99349bc', 'b8f0c994-55d8-4c8a-a989-5a08c6c8caa3', 'Whether motorable road is available around tank farm', 'Motorable road has not been provided around tank farm area', 'Motorable road has not been provided around tank farm area', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'DESIGN RELATED', 'Land to be acquired adjacent to our depot for which land not available , since there is no space to put motorable road around the tank farm. Hence request already put to consider dropping of this point .', 6, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('b9c677da-1964-476a-af28-d6a362d70117', 'd138c049-baab-4690-b1c6-ad96576381e0', 'Industry Tank Wagon Unloading Siding', 'RCC apron have not been provided at the siding. Enter siding area is oil socked. Settlement of railway track has also taken place. No certificate is available on record for fitness of the railway track but rakes are being placed round the clock. Ground water pollution is taking place on continuous basis.', 'Corrective action to be taken for upgradation of industry TW siding and ground water pollution must be stopped through proper drainage/OWS system.', 'PENDING', '2017-12-31 00:00:00', '2017-12-31 00:00:00', 0, 'DESIGN RELATED', 'Apart from Ballast which is to be done by Railways, the impervious siding, Drains and OWS is being constrcated by IOC.', 8, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('bc4329b2-ae97-46e5-8a8a-d23245d2a21d', 'fc4bdd3d-4cd6-4f11-a7fe-12fe5e306a03', 'Whether escape ladders and identification signs for escape ladders are provided.', 'Monkey ladders and stair case are provided at intervals ( 6 nos ) along the gantry facilitating exit of men to ground. However at some stair cases identification signs are not available.', 'Crossover  to be provided across the spur for easy access of men towards terminal and FCI godown premises and for safety and rescue.', 'PENDING', '2017-12-31 00:00:00', '2017-12-31 00:00:00', 0, 'OTHERS', 'Budget proposals already sent in NPCB 2015-16,2016-17 and 2017-18, but got rejected. Work will be completed once budget is obtained', 1, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('bc9936d1-ed1a-43a1-a8fa-7d6c897055d4', '25d35a2e-04fd-4a00-a2b2-dbbd54c4c62b', 'Whether tanks are cleaned as per Industry QC manual.', 'Tank no. 5 due for cleaning in June 2014. Tank no. 6 is having contaminated stock and hence cleaning could not be undertaken.', 'To be expedited.', 'PENDING', '2017-08-31 00:00:00', '2017-08-31 00:00:00', 0, 'MAINTENANCE', 'T-5 cleaned. FO tank no T-6 is having off spec stock of 1470 KL. This stock is planned to be evacuated and sent to Kochi refinery.', 0, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('bcc03cec-88b9-45c0-9ffd-375d147a76a2', '5129cfec-f580-4173-bf41-eed305ce5620', 'Location of HVLRs w.r.t. tanks', '5 HVLRs installed covering the tanks in common dyke (housing tanks 5,6,7,8,9 & 14 - 5 & 14 being the MS tnaks).  Remote operation was checked of 2 HVLRs and found functional.   However, HVLR 2 near TK14 (MS) was installed at 34 meter distance .  HVLR 3 near TK7 was installed at 34.6 meter distance, HVLR 4 near TK14 at 35.8 meter distance and HVLR5 near TK 5 (MS) was installed at 27.6 meter distance.', 'HVLR panel to be located minimum 60 meters away from the hazard to be oprotected.', 'PENDING', '2017-05-31 00:00:00', '2017-06-30 00:00:00', 0, 'MBLR', 'E&P WORK IN PROGRESS', 5, null, true);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('bd2cdf4a-0dae-414a-85c8-174f9dcce37e', '5129cfec-f580-4173-bf41-eed305ce5620', 'OWS system in depot', 'There are 5 OWS in the depot, out of which 4 inside hazardous area and 1 in non hazardous area near contol room (under commissioning).  Drain from 4 OWS of operating areas routed to the 5th OWS located in non-hazardous area in proximity to control room .', 'OWS to be shifted to hazardous area keeping minimum 30 meters distance from control room.', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'DESIGN RELATED', 'PESO approval to be applied and budgetary estimates to be prepared.', 7, null, false);
