INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('c60f1641-cf8f-4f77-9e99-de5da047d34a', '3cb35de9-5c68-46fb-9694-2efda987e761', 'Whether any provision  for tank lorry parking', 'There is no provision for proper parking of tank lorries', 'Tank Lorry Parking Area to be provided', 'PENDING', '2017-03-31 00:00:00', '2017-03-31 00:00:00', 0, 'DESIGN RELATED', 'Village Committee had agreed to give a piece of land in survey no: 490(4000 Sq, Mt) on lease,  on a monthly rental of Rs.30000/- for period of 20 years. The file was cleared by Collectors'' Office and the file was forwarded to Gandhinagar Office by the Collector for approval for seeking approval from Gandhinagar- revenue Department. However due to revision of Tax rates (Jantri Rates) in Gujarat, the file was returned for reassessment. Collector Vadodara vide his letter dt.18/2/13 sought the valuation of the proposed land from Mamlatdar,Rural,Vadodara. matter still pending with Collector office.On 22/8/14 we recd. a copy of the letter from Tasildar ,Vadodara to Sub-Register, Vadodara seeking valuation details of the adjacent area. The required details were submitted to Mamlatdar vide our letter dt. 1.9.14. Vide letter dated 11/12/14 Mamlatdar (Rural), Vadodara recd. on 20/12/14 sought the details again. Location has submitted the documents with our covering letter Ref. KI.Parking dt.07-01-15. file is now pending with  Collector, Vadodara for further action.On 06.06.2015 mamlatdar and Tahsilder visisted the site for inspections and collected relevent documnet from location for onwards submission to Collector Vadodara.', 0, null, false);
INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('c6847e93-300e-4f88-b615-284f24dbccaf', '76c9381e-690f-4728-8746-c64f59915256', 'Misc', '1. Fire Hose should be tested as per OISD 135 & records to be maintained.                                                                            2. Lab : No dedicated sample room in Lab.  Samples are kept where non FLP fittings are existing.                   3. Location PESO approved drawing does not contain consumer Pump but consumer pump is having independent lic.', '1.To comply                                                                       2. Dedicated sample room with FLP fittings are to be made available.                                 3. Consumer Pump along with 10 KL tank to be included in the main PESO approved drawing.', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'OTHERS', 'Point 1 & 3 complied.                                                                            Point 2 - Awaiting budget for completion of work.', 3, null, false);
