INSERT INTO hsse.esa_audit_item (id, esa_audit_id, audit_point, observation, recommendation, status, target_date, revised_date, date_change_count, category, comments, sequence_id, compliance_date, extension_required) VALUES ('ca268fd1-7a2b-4afc-97f7-210ca8c51649', '9cfcc03f-f1c4-4a20-b0a4-dbde0f8f9a47', 'Whether location applied / approached/Obtained prohibited area/place Notification from District Authority ?', 'Location has applied to district authority in 2008 , inspection by revenue department, Fire department & police department completed and reported submitted to district collector office.', 'Location to co ordinate with district authority for obtaining the prohibited area declaration', 'PENDING', '2017-06-30 00:00:00', '2017-06-30 00:00:00', 0, 'STATUTORY', 'File moved from Joint collector to Collector,follow up in progress. Last followed up at collector office by Mr. K Madhavaiah on 28.02.2017. They have assured to forward it to secreteriat by 10.03.2017', 0, null, false);
